import base64
import config

cookie_base64_string = ''

class CookieManager:
    def __init__(self):
        self.file_path = config.COOKIE_PATH


    def get_cookie(self):
        cookie_base64_binary = cookie_base64_string.encode('ascii')
        cookie_decoded_binary = base64.b64decode(cookie_base64_binary)
        cookie_decoded_string = cookie_decoded_binary.decode('ascii')

        self._write_to_file(cookie_decoded_string)
        return self.file_path
    
    def _write_to_file(self, string):
        with open(self.file_path, 'w') as f:
            f.write(string)